import React, { useState, useEffect } from 'react';

const RoleBade = ({role}) => {
    const [ color, setColor ] = useState('');


    useEffect(() => {
        switch(role){
            case 'Initiator':
                setColor('yellow');
        }
    }, [])


    return (
        <div className={`badge ${role} ${color}`}>
            <p className='ribbon'>{role}</p>
        </div>
    )
}

export default RoleBade;
