import React, { useState, useEffect } from 'react';
import axios from 'axios';

const HeroCard = ({hero}) => {
    const [ img, setImg ] = useState(null);
    const proxyurl = "https://cors-anywhere.herokuapp.com/";

    useEffect(() => {
        console.log(`${proxyurl}https://api.opendota.com${hero.img}`)
        axios.get(`${proxyurl}https://api.opendota.com${hero.img}`)
        .then(res => {
            console.log(res);
            setImg(res.data);
        })
        .catch(err => {
            console.log(err);
        })
    }, [])

    return (
        <div>
            {img && <img src={img}></img>}
            <h1>{hero.localized_name}</h1>
        </div>
    )
}

export default HeroCard;
