import React, { useState } from 'react';

const RoleBade = ({role}) => {
    const [ color, setColor ] = useState('');

    switch(role){
        case Initiator:
            setColor('yellow');
    }

    return (
        <div className={`badge ${role} ${color}`}>
            <p className='ribbon'>{role}</p>
        </div>
    )
}

export default RoleBade;
